import React, { useState } from 'react';
import { ITabsProps } from '../types/types';

const Tabs = (props: ITabsProps) => {

    const {
        defaultActiveTabIndex,
        children
    } = props

    const [activeTabIndex, setActiveTabIndex] = useState(defaultActiveTabIndex)


    function handleTabClick(tabIndex: any) {
        setActiveTabIndex({
            //@ts-ignore
            activeTabIndex: tabIndex === activeTabIndex ? defaultActiveTabIndex : tabIndex
        });
    }

    function renderActiveTabContent() {
        //@ts-ignore
        if (children[activeTabIndex]) {
            //@ts-ignore
            return children[activeTabIndex].props.children;
        }
    }
    function renderChildrenWithTabsApiAsProps() {
        return React.Children.map(children, (child, index) => {
            return React.cloneElement(child, {
                onClick : handleTabClick,
                tabIndex: index,
                isActive: index === activeTabIndex
            });
        });
    }
    return (
        <>
            <div className="tabs">
                <ul className="tabs-nav nav navbar-nav navbar-left">
                    {renderChildrenWithTabsApiAsProps()}
                </ul>
                <div className="tabs-active-content">
                    {renderActiveTabContent()}
                </div>
            </div>
        </>
    )
}

export default Tabs;
