import React, { useState } from 'react';
import { ITabProps } from '../types/types';

const Tab = (props: ITabProps) => {

    const {
        linkClassName,
        isActive,
        tabIndex,
        iconClassName,
        onClick
    } = props

    
    return (
        <>
            <li className="tab">
                <a className={`tab-link ${linkClassName} ${isActive ? 'active' : ''}`}
                    onClick={(event) => {
                        event.preventDefault();
                        onClick!(tabIndex);
                    }}>
                    <i className={`tab-icon ${iconClassName}`} />
                </a>
            </li>
        </>
    )
}

export default Tab;
