import React, { useEffect, useState } from 'react';
import { IChartProps } from '../types/types';
import { Doughnut } from 'react-chartjs-2';
import Loading from './Loading';

const Chart = (props: IChartProps) => {

    const [labels, setLabels] = useState([]);
    const [chartData, setChartData] = useState([]);
    const [fetchChart, setFetchChart] = useState(false);
    const [url, setUrl] = useState()

    const {
        id,
        colors,
        hoverColors,
        urlToFetch,
        chartHeading
    } = props;

    const fetchData = () => fetch(url)
        .then(response => response.json())
        .then(data => {
            const obj = data[0];
            const values: number[] = Object.keys(obj).map(key => { return obj[key]; });
            const label: string[] = Object.keys(obj).map(key => { return key; });
            if (values[0] === null) {
                return;
            }
            else {
                //@ts-ignore
                setLabels(label)
                //@ts-ignore
                setChartData(values)
            }
        })
        .then(() => setFetchChart(true))

    useEffect(() => {
        setUrl(urlToFetch)
        fetchData()
    }, [urlToFetch])

    return (
        <>

            {
                fetchChart ?
                    <>
                        <p>{chartHeading}</p>
                        <Doughnut
                            key={id}
                            data={{
                                labels: labels,
                                datasets: [{
                                    data: chartData,
                                    backgroundColor: colors,
                                    borderWidth: 0,
                                    hoverBackgroundColor: hoverColors,
                                    hoverBorderWidth: 2,
                                }]
                            }}
                        />
                    </>
                    :
                    <Loading />

            }
        </>
    )
}

export default Chart;
