import React, { useEffect, useState } from 'react';
import { IChartProps } from '../types/types';
import { Doughnut } from 'react-chartjs-2';
import { proxyUrls } from '../utils/MapConfig';
import Loading from './Loading';

const Chart = (props: IChartProps) => {

    const [labels, setLabels] = useState([]);
    const [chartData, setChartData] = useState([]);
    const [fetchChart, setFetchChart] = useState(false);

    const { colors } = props;

    useEffect(() => {
        fetch(`${proxyUrls[5]}`)
            .then(response => response.json())
            .then(data => {
                const obj = data[0];
                const values: number[] = Object.keys(obj).map(key => { return obj[key]; });
                const label: string[] = Object.keys(obj).map(key => { return key; });
                 //@ts-ignore
                setLabels(label)
                //@ts-ignore
                setChartData(values)
            })
            .then(() => setFetchChart(true))
    }, [])

    return (
        <>
            <p>Anteil besuchter und unbesuchter Geb√§ude im gesamten Stadtgebiet (%) </p>
            {fetchChart ? 
                <Doughnut
                    data={{
                        labels: labels,
                        datasets: [{
                            data: chartData,
                            backgroundColor: colors,
                        }]
                    }}
                />
                :
                <Loading />
            }
        </>
    )
}

export default Chart;
