import React, { useEffect, useState } from 'react';
import { IDistrictInfoProps } from '../types/types';
import Button from './Button'
import { proxyUrls } from '../utils/MapConfig';

const DistrictInfo = (props: IDistrictInfoProps) => {

    const [district, setDistrict] = useState(undefined);
    const [fetched, setFetched] = useState(false);

    const {
        title,
        fetchUrl,
        defaultText,
    } = props

    function buttonClick() {
        fetch(proxyUrls[4])
            .then(response => response.json())
            .then(data => setDistrict(data[0]))
            .then(() => setFetched(true))
    }

    useEffect(() => {
        let dropdown = document.createElement('select');
        dropdown.length = 0;

        let defaultOption = document.createElement('option');
        defaultOption.text = defaultText;

        dropdown.add(defaultOption);
        dropdown.selectedIndex = 0;
        document.getElementsByClassName('dropdown')[0].appendChild(dropdown);

        const url = fetchUrl;

        fetch(url)
            .then(
                function (response) {
                    if (response.status !== 200) {
                        console.log('Status Code not 200: ' +
                            response.status);
                        return;
                    }

                    response.json().then(function (data) {
                        let option;

                        for (let i = 0; i < data.length; i++) {
                            option = document.createElement('option');
                            option.text = data[i].stt_name;
                            option.value = data[i].stt_name;
                            dropdown.add(option);
                        }
                    });
                }
            )
            .catch(function (err) {
                console.error(err);
            });
    }, [])

    return (
        <>
            <p>{title}</p>
            <div className='dropdown'></div>
            <Button
                text='Stadtteildaten abrufen'
                clickButton={buttonClick}
            />
            {fetched ? 
             <p>{district}</p>
             :
             <p>Keine Daten.</p>
            }
        </>
    )
}

export default DistrictInfo;
