import React, { useState, useEffect } from 'react';
import { IUserInfoProps } from '../types/types';
import Loading from './Loading';

const UserInfo = (props: IUserInfoProps) => {

    const [userCount, setUserCount] = useState(0);
    const [changeCount, setChangeCount] = useState(0);
    const [fetchedCount, setFetchtedCount] = useState(false);

    const { heading } = props;

    useEffect(() => {
        let changes: number = 0;
        fetch('http://192.168.2.185:8000/userInfo')
            .then(data => data.json())
            .then(res => {
                setUserCount(res.length)
                for (let i = 0; i < res.length; i++) {
                    changes += parseInt(res[i].count);
                    console.log(changes);
                }
            })
            .then(() => setChangeCount(changes))
            .then(() => setFetchtedCount(true))
    }, [])


    function renderUsers() {
        return [...Array(userCount)].map((e, i) => <i key={i} className='fa fa-user fa-2x'></i>)
    }

    function renderChanges() {
        return [...Array(changeCount)].map((e, i) => <i key={i} className='fa fa-edit'></i>)
    }

    function render() {
        renderUsers()
        renderChanges()
    }

    return (
        <>
            <div>
                <p>{heading}</p>
                {
                    fetchedCount ?
                        <>
                        <b>{userCount}</b> <p>Nutzer haben Daten beigesteuert.</p>{renderUsers()}<br></br>
                        <b>{changeCount}</b> <p>Hausumringe sind editiert worden.</p>{renderChanges()}
                        <b>{changeCount/userCount}</b> <p>im Schnitt</p>
                        </>
                        :
                        <Loading />
                }
            </div>
        </>
    )
}

export default UserInfo;
