import React, { useState, useEffect } from 'react';
import 'ol/ol.css';
import { IMapProps, IMapState } from '../types/types';
import { MapUtils } from '../utils/MapUtils';
import { useDispatch, useSelector } from 'react-redux';
import { setMap } from '../redux/actions/setMapAction';
import { RootState } from '../redux/store/store';
import Loading from './Loading';

const Basemap = (props: IMapProps) => {

    const map = useSelector((state: RootState) => state.currentMapState.map)
    const fetchingProgress = useSelector((state: RootState) => state.currentFetchState.fetching)
    const dispatch = useDispatch();

    const { id, mapContainer, proxyUrl } = props

    useEffect(() => {
        const basemap = MapUtils.createMap(mapContainer)
        //@ts-ignore
        dispatch(setMap(basemap))
        MapUtils.createPgLayer(proxyUrl, basemap)
    }, []);

    return (
        <> {
            fetchingProgress ?
                <Loading />
                :
                <div id={id} className='map'></div>
        }
        </>
    )
}

export default Basemap;
