import React, { useState, useEffect } from 'react';
import { IUserInfoProps } from '../types/types';

const UserInfo = (props: IUserInfoProps) => {

    const [userCount, setUserCount] = useState(0);
    const [changeCount, setChangeCount] = useState(5);
    const [fetchedCount, setFetchtedCount] = useState(false);

    const { heading } = props;

    useEffect(() => {
        let changes: number;
        fetch('http://192.168.2.185:8000/userInfo')
            .then(data => data.json())
            .then(res => {
                setUserCount(res.length)
                for (let i = 0; i < res.length; i++) {
                    changes = res[i].count;
                    console.log(changes)
                }
            })
            .then(() => setChangeCount(changes))
    }, [])

   
    function renderUsers() {
        return [...Array(userCount)].map((e, i) => <i key={i} className='fa fa-user fa-2x'></i>)
    }

    function renderChanges() {
        return [...Array(changeCount)].map((e, i) => <i key={i} className='fa fa-handshake-o fa-2x'></i>)
    }

    return (
        <>
            <div>
                <p>{heading}</p>
                <i className='fa fa-user'></i>
                {renderUsers()}
                {renderChanges()}
            </div>
        </>
    )
}

export default UserInfo;
